CREATE TABLE `dkp_news` (`news_id`  smallint(5) NOT NULL AUTO_INCREMENT ,`news_headline`  varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' ,`news_message`  text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL ,`news_date`  int(11) NOT NULL ,`nocomments`  smallint(4) NOT NULL ,`news_permissions`  tinyint(3) UNSIGNED NOT NULL ,`news_flags`  smallint(4) UNSIGNED NOT NULL DEFAULT 0 ,`user_id`  smallint(5) NOT NULL ,PRIMARY KEY (`news_id`),FOREIGN KEY (`user_id`) REFERENCES `dkp_user` (`user_id`) ON DELETE RESTRICT ON UPDATE CASCADE,UNIQUE INDEX `news_id` USING BTREE (`news_id`) ,UNIQUE INDEX `news_date` USING BTREE (`news_date`) ,INDEX `user_id` USING BTREE (`user_id`) ,INDEX `news_flag` USING BTREE (`news_flags`) )ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_binAUTO_INCREMENT=1ROW_FORMAT=COMPACT;